import React from 'react';

class MyComponent extends React.Component {

   constructor(props) {
      super(props);
      this.state = {
        error: null,
        isLoaded: false,
        items: [{"idDepartement":1,"nomDepartement":"Informatique","nomDept_court":"Info","idUFR":1},{"idDepartement":2,"nomDepartement":"Sc. De la terre","nomDept_court":"Sc. De la terre","idUFR":1},{"idDepartement":3,"nomDepartement":"Sc De la vie","nomDept_court":"Sc De la vie","idUFR":1}]
      };
    }


    componentDidMount() {

      fetch("http://localhost:7179/depart")
        .then(res => res.json())
        .then(
          (result) => {
            this.setState({
              isLoaded: true,
              items: result.items
            });
          },
          // Remarque : il est important de traiter les erreurs ici
          // au lieu d'utiliser un bloc catch(), pour ne pas passer à la trappe
          // des exceptions provenant de réels bugs du composant.
          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
        )


    }

    render() {

      const { error, isLoaded, items } = this.state;

        console.log(items);

      if (error) {
        return <div>Erreur : {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Chargement…</div>;
      } else {
        return (
          <ul>
            {items.map(item => (
              <li key={item.idDepartement}>
                {item.nomDepartement} {item.idUFR}
              </li>
            ))}
          </ul>
        );
      }
    }

  }

  export default MyComponent;
